#ifndef SYMPHONYINSTPRINTER_H
#define SYMPHONYINSTPRINTER_H

#include "llvm/MC/MCInstPrinter.h"
#include "MCTargetDesc/SymphonyMCTargetDesc.h"

namespace llvm {
class SymphonyInstPrinter : public MCInstPrinter {
public:
  SymphonyInstPrinter(const MCAsmInfo &MAI, const MCInstrInfo &MII,
                      const MCRegisterInfo &MRI)
      : MCInstPrinter(MAI, MII, MRI) {}

private:
  // Autogenerated by tblgen.
  void printInstruction(const MCInst *MI, uint64_t Address, raw_ostream &O);
  static const char *getRegisterName(MCRegister Reg);

  void printRegName(raw_ostream &OS, MCRegister Reg) override;
  void printInst(const MCInst *MI, uint64_t Address, StringRef Annot,
                 const MCSubtargetInfo &STI, raw_ostream &O) override;

  bool printAliasInstr(const MCInst *MI, uint64_t Address, raw_ostream &OS);
  void printCustomAliasOperand(const MCInst *MI, unsigned OpIdx,
                               unsigned PrintMethodIdx, raw_ostream &O);

  std::pair<const char *, uint64_t>
  getMnemonic(const MCInst &MI) const override;

private:
  void printOperand(const MCInst *MI, int opNum, raw_ostream &OS);
  void printImmediate(const MCInst *MI, unsigned opNum, raw_ostream &O);
};
} // namespace llvm

#endif // SYMPHONYINSTPRINTER_H
